CREATE TABLE IF NOT EXISTS hiv_enrollment ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR ( 255 ), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR ( 255 ), facility_id BIGINT, unique_id VARCHAR ( 255 ), entry_point_id BIGINT, target_group_id BIGINT, date_confirmed_hiv date, date_enrolled_pmtct date, source_of_referrer_id BIGINT, time_hiv_diagnosis TIMESTAMP with time zone, pregnant BOOLEAN, breastfeeding BOOLEAN, date_of_registration date, status_at_registration_id BIGINT, enrollment_setting_id BIGINT, date_started date, send_message BOOLEAN, person_uuid VARCHAR (255) NOT NULL, visit_id VARCHAR ( 255 ) NOT NULL, uuid VARCHAR ( 255 ) NOT NULL, archived INTEGER, facility_name VARCHAR ( 255 ), ovc_number VARCHAR ( 255 ), house_hold_number VARCHAR ( 255 ), care_entry_point_other VARCHAR ( 255 ), referred_to_OVCPartner VARCHAR ( 255 ), referred_from_OVCPartner VARCHAR ( 255 ), date_referred_to_OVCPartner date, date_referred_from_OVCPartner date, date_of_lpm date, pregnancy_status_id BIGINT, tb_status_id BIGINT, CONSTRAINT pk_hiv_enrollment PRIMARY KEY ( id ) );
ALTER TABLE hiv_enrollment ADD CONSTRAINT uc_hiv_enrollment_uuid UNIQUE (uuid);
ALTER TABLE hiv_enrollment ADD CONSTRAINT FK_HIV_ENROLLMENT_ON_PERSON_UUID FOREIGN KEY (person_uuid) REFERENCES patient_person (uuid);
ALTER TABLE hiv_enrollment ADD CONSTRAINT FK_HIV_ENROLLMENT_ON_VISIT FOREIGN KEY (visit_id) REFERENCES patient_visit (uuid);

CREATE TABLE IF NOT EXISTS hiv_art_clinical ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR ( 255 ), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR ( 255 ), facility_id BIGINT, visit_date date, cd_4 BIGINT, cd_4_percentage BIGINT, is_commencement BOOLEAN, functional_status_id BIGINT, clinical_stage_id BIGINT, clinical_note VARCHAR ( 255 ), uuid VARCHAR ( 255 ) NOT NULL, hiv_enrollment_uuid VARCHAR ( 255 ) NOT NULL, regimen_id BIGINT, regimen_type_id BIGINT, art_status_id BIGINT NOT NULL, archived INTEGER, vital_sign_uuid VARCHAR ( 255 ) NOT NULL, who_staging_id BIGINT, person_uuid VARCHAR ( 255 ) NOT NULL, visit_id VARCHAR ( 255 ) NOT NULL, oi_screened VARCHAR ( 255 ), sti_ids VARCHAR ( 255 ), sti_treated VARCHAR ( 255 ), opportunistic_infections JSONB, adr_screened VARCHAR ( 255 ), adverse_drug_reactions JSONB, adherence_level VARCHAR ( 255 ), adheres JSONB, next_appointment date, lmp_date date, tb_screen JSONB, is_viral_load_at_start_of_art BOOLEAN, viral_load_at_start_of_art DOUBLE PRECISION, date_of_viral_load_at_start_of_art date, CONSTRAINT pk_hiv_art_clinical PRIMARY KEY ( id ) );
ALTER TABLE hiv_art_clinical ADD CONSTRAINT uc_hiv_art_clinical_uuid UNIQUE (uuid);
