CREATE TABLE IF NOT EXISTS hiv_status_tracker ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR ( 255 ), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR ( 255 ), facility_id BIGINT, hiv_status VARCHAR ( 255 ) NOT NULL, status_date date NOT NULL, person_id VARCHAR ( 255 ) NOT NULL, visit_id VARCHAR ( 255 ) NOT NULL, tracking_outcome VARCHAR ( 255 ), track_date date, agreed_date date, reason_for_interruption VARCHAR ( 255 ), cause_of_death VARCHAR ( 255 ), auto BOOLEAN, uuid VARCHAR ( 255 ), archived INTEGER, CONSTRAINT pk_hiv_status_tracker PRIMARY KEY ( id ) );
