changeSet.sql:
UPDATE prep_regimen
    SET regimen = 'TDF(300mg)+3TC(300mg)'
    AND code = 'TDF(300mg)+3TC(300mg)'
WHERE regimen = 'TDF(300mg)+3TC(150mg)';

INSERT INTO prep_regimen(regimen, composition, description, archived, code)
VALUES ('CAB-LA(600mg/3mL)', 'Cabotegravir', 'Pre-Exposure Prophylaxis Injectable (PrEP Injectable)', 1, 'CAB-LA(600mg/3mL)');
ALTER TABLE prep_regimen
    ADD COLUMN prep_type character varying;
UPDATE prep_regimen SET prep_type = 'ORAL' WHERE regimen = 'TDF(300mg)+3TC(300mg)';
UPDATE prep_regimen SET prep_type = 'INJECTABLE' WHERE regimen = 'CAB-LA(600mg/3mL)';
